import React, { useState, useEffect } from 'react';
import { Edit, Trash2, Plus, Download, Clock, Play, Pause, Search } from 'lucide-react';
import { agentStatusClass } from '../utils/format'; // adjust path if needed

const AgentsTable = ({
  user,
  agentSearch,
  setAgentSearch,
  showAgentExport,
  setShowAgentExport,
  filteredAgents,
  exportToExcel,
  handleEditAgent,
  handleDeleteAgent,
  handleStatusChange,
  handleBreakStatusChange,
  agentBreakHistory,
  agentWorkingStatus,
}) => {
  const [selectedAgent, setSelectedAgent] = useState(null);
  const [showBreakModal, setShowBreakModal] = useState(false);
  const [breakData, setBreakData] = useState({ fromTime: '', toTime: '', remark: '' });

  const isAdmin = user?.role === 'admin';
  const isAgent = user?.role === 'agent';

  const handleBreakClick = (agent) => {
    setSelectedAgent(agent);
    setShowBreakModal(true);
    setBreakData({ fromTime: '', toTime: '', remark: '' });
  };

  const isValidFromTime = (time) => {
    const [hours, minutes] = time.split(':').map(Number);
    const now = new Date();
    const currentMinutes = now.getHours() * 60 + now.getMinutes();
    const inputMinutes = hours * 60 + minutes;
    return inputMinutes >= currentMinutes;
  };

  const isValidTimeRange = () => {
    const [fromH, fromM] = breakData.fromTime.split(':').map(Number);
    const [toH, toM] = breakData.toTime.split(':').map(Number);
    const fromMinutes = fromH * 60 + fromM;
    const toMinutes = toH * 60 + toM;
    return toMinutes > fromMinutes;
  };

  const startBreak = () => {
    if (selectedAgent) {
      const breakRecord = {
        agent_number: selectedAgent.agent_number,
        from_time: breakData.fromTime,
        to_time: breakData.toTime,
        remark: breakData.remark,
        date: new Date().toISOString().split('T')[0],
      };
      console.log('Break record to be saved:', breakRecord);
      saveBreakRecord(breakRecord); // Add this line to call the backend function
      handleBreakStatusChange(selectedAgent.agent_number, 'break', breakRecord);
      setShowBreakModal(false);
    }
  };

  return (
    <div className="overflow-x-auto">
      {isAdmin && (
        <div className="flex justify-between items-center mb-4">
          <div className="relative w-1/2">
            <Search className="absolute left-3 top-2.5 text-gray-400" size={18} />
            <input
              type="text"
              placeholder="Search agent..."
              value={agentSearch}
              onChange={(e) => setAgentSearch(e.target.value)}
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-md w-full"
            />
          </div>
          <div className="flex space-x-2">
            <button
              onClick={() => setShowAgentExport(!showAgentExport)}
              className="flex items-center px-3 py-2 border text-sm font-medium rounded-md bg-gray-100 hover:bg-gray-200"
            >
              <Download className="mr-2" size={16} /> Export
            </button>
            <button
              onClick={() => handleEditAgent(null)}
              className="flex items-center px-3 py-2 border text-sm font-medium rounded-md bg-blue-500 text-white hover:bg-blue-600"
            >
              <Plus className="mr-2" size={16} /> Add Agent
            </button>
          </div>
        </div>
      )}

      <table className="min-w-full bg-white border">
        <thead>
          <tr className="bg-gray-100 text-left">
            <th className="px-4 py-2 border">#</th>
            <th className="px-4 py-2 border">Name</th>
            <th className="px-4 py-2 border">Number</th>
            <th className="px-4 py-2 border">Status</th>
            {isAdmin && <th className="px-4 py-2 border">Work Status</th>}
            {isAdmin && <th className="px-4 py-2 border">Last Break</th>}
            <th className="px-4 py-2 border">Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredAgents.map((agent, index) => {
            if (isAgent && agent.agent_number !== user.agent_number) return null;
            const lastBreak = agentBreakHistory[agent.agent_number];
            const workStatus = agentWorkingStatus[agent.agent_number] || 'Working';

            return (
              <tr key={agent.agent_number} className="hover:bg-gray-50">
                <td className="px-4 py-2 border">{index + 1}</td>
                <td className="px-4 py-2 border">{agent.name}</td>
                <td className="px-4 py-2 border">{agent.agent_number}</td>
                <td className={`px-4 py-2 border ${agentStatusClass(agent.status)}`}>{agent.status}</td>
                {isAdmin && <td className="px-4 py-2 border">{workStatus}</td>}
                {isAdmin && (
                  <td className="px-4 py-2 border text-sm text-gray-600">
                    {lastBreak?.remark ? (
                      <>
                        <div>{lastBreak.remark}</div>
                        <div className="text-xs text-gray-500">
                          {lastBreak.from_time} - {lastBreak.to_time}
                        </div>
                      </>
                    ) : (
                      '—'
                    )}
                  </td>
                )}
                <td className="px-4 py-2 border">
                  {isAdmin ? (
                    <div className="flex space-x-2">
                      <button onClick={() => handleEditAgent(agent)}>
                        <Edit className="text-blue-500 hover:text-blue-700" size={18} />
                      </button>
                      <button onClick={() => handleDeleteAgent(agent.agent_number)}>
                        <Trash2 className="text-red-500 hover:text-red-700" size={18} />
                      </button>
                      <button onClick={() => handleStatusChange(agent.agent_number)}>
                        {agent.status === 'Active' ? (
                          <Pause className="text-yellow-500 hover:text-yellow-700" size={18} />
                        ) : (
                          <Play className="text-green-500 hover:text-green-700" size={18} />
                        )}
                      </button>
                    </div>
                  ) : (
                    <button onClick={() => handleBreakClick(agent)} className="text-sm px-2 py-1 bg-yellow-100 border rounded">
                      Start Break
                    </button>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      {showBreakModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-md w-96">
            <h2 className="text-lg font-semibold mb-4">Break Details</h2>
            <label className="block mb-2 text-sm">From Time:</label>
            <input
              type="time"
              value={breakData.fromTime}
              onChange={(e) => setBreakData({ ...breakData, fromTime: e.target.value })}
              className="w-full px-3 py-2 border rounded mb-2"
            />
            {breakData.fromTime && !isValidFromTime(breakData.fromTime) && (
              <p className="text-red-500 text-xs mb-2">⚠️ Time should not be in the past</p>
            )}
            <label className="block mb-2 text-sm">To Time:</label>
            <input
              type="time"
              value={breakData.toTime}
              onChange={(e) => setBreakData({ ...breakData, toTime: e.target.value })}
              className="w-full px-3 py-2 border rounded mb-2"
            />
            {breakData.toTime && !isValidTimeRange() && (
              <p className="text-red-500 text-xs mb-2">⚠️ To Time must be after From Time</p>
            )}
            <label className="block mb-2 text-sm">Remark:</label>
            <input
              type="text"
              value={breakData.remark}
              onChange={(e) => setBreakData({ ...breakData, remark: e.target.value })}
              className="w-full px-3 py-2 border rounded mb-4"
            />
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => setShowBreakModal(false)}
                className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
              >
                Cancel
              </button>
              <button
                onClick={startBreak}
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                disabled={
                  !breakData.fromTime ||
                  !breakData.toTime ||
                  !breakData.remark ||
                  !isValidFromTime(breakData.fromTime) ||
                  !isValidTimeRange()
                }
              >
                Start
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AgentsTable;
