import React, { useState, useEffect } from 'react';
import { Edit, Trash2, Plus, Download, Clock, Play, Pause } from 'lucide-react';
import { agentStatusClass } from '../utils/format'; // adjust path if needed

const AgentsTable = ({
  user,
  agentSearch,
  setAgentSearch,
  showAgentExport,
  setShowAgentExport,
  filteredAgents,
  exportToExcel,
  exportToPDF,
  exportToCSV,
  exportToXML,
  setShowAddModal,
  setFormData,
  setError,
  userRoleIsAdmin,
  openEditModal,
  handleDeleteAgent,
  deleteLoading,
  showAddModal,
  showEditModal,
  formData,
  setShowEditModal,
  handleAddAgent,
  handleEditAgent,
  loading,
  error
}) => {
  const [activeStatus, setActiveStatus] = useState('Active');
  const [breakData, setBreakData] = useState({
    fromTime: '',
    toTime: '',
    remark: ''
  });
  const [isOnBreak, setIsOnBreak] = useState(false);
  const [breakStartTime, setBreakStartTime] = useState(null);
  const [breakTimer, setBreakTimer] = useState('00:00:00');
  const [showBreakModal, setShowBreakModal] = useState(false);

  // Filter agents based on user role
  const displayAgents = userRoleIsAdmin 
    ? filteredAgents 
    : filteredAgents.filter(agent => agent.agent_number === user?.agent_number);

  // Get current time in HH:MM format
  const getCurrentTime = () => {
    const now = new Date();
    return now.toTimeString().slice(0, 5);
  };

  // Set current time as default from time when modal opens
  useEffect(() => {
    if (showBreakModal) {
      setBreakData(prev => ({
        ...prev,
        fromTime: getCurrentTime()
      }));
    }
  }, [showBreakModal]);

  // Timer effect for break countdown
  useEffect(() => {
    let interval = null;
    if (isOnBreak && breakStartTime) {
      interval = setInterval(() => {
        const now = new Date();
        const diff = now.getTime() - breakStartTime.getTime();
        const hours = Math.floor(diff / (1000 * 60 * 60));
        const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((diff % (1000 * 60)) / 1000);
        setBreakTimer(`${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isOnBreak, breakStartTime]);

  const handleActiveStatusChange = (status) => {
    if (status === 'Break') {
      setShowBreakModal(true);
    } else if (status === 'Active' && isOnBreak) {
      // Calculate total break duration and push to backend
      const endTime = new Date();
      const totalDuration = Math.floor((endTime.getTime() - breakStartTime.getTime()) / 1000); // in seconds
      
      // Prepare break record for backend
      const breakRecord = {
        agent_number: user.agent_number,
        break_start: breakStartTime.toISOString(),
        break_end: endTime.toISOString(),
        duration: totalDuration,
        remark: breakData.remark,
        from_time: breakData.fromTime,
        to_time: breakData.toTime
      };
      
      console.log('Break record to be saved:', breakRecord);
      // TODO: Replace with actual API call
      // await saveBreakRecord(breakRecord);
      
      // Reset break state
      setIsOnBreak(false);
      setBreakStartTime(null);
      setBreakTimer('00:00:00');
      setBreakData({ fromTime: '', toTime: '', remark: '' });
      setActiveStatus('Active');
    } else {
      setActiveStatus(status);
    }
  };

  const startBreak = () => {
    if (breakData.fromTime && breakData.toTime && breakData.remark) {
      setIsOnBreak(true);
      setBreakStartTime(new Date());
      setActiveStatus('Break');
      setShowBreakModal(false);
    }
  };

  // Validate that from time is not in the past
  const isValidFromTime = (timeString) => {
    if (!timeString) return true; // Allow empty for now
    
    const now = new Date();
    const currentTime = now.getHours() * 60 + now.getMinutes();
    const [hours, minutes] = timeString.split(':').map(Number);
    const selectedTime = hours * 60 + minutes;
    
    return selectedTime >= currentTime;
  };

  return (
    <div className="bg-white shadow rounded-lg">
      <div className="px-6 py-4 border-b border-gray-200 flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <h2 className="text-lg font-medium text-gray-900">
          {userRoleIsAdmin ? 'Agent Management' : 'My Profile'}
        </h2>
        <div className="flex flex-col sm:flex-row sm:items-center gap-2 mt-2 sm:mt-0">
          {/* Active Status Button - Only for Non-Admin Users */}
          {!userRoleIsAdmin && (
            <div className="flex items-center gap-2">
              <label className="text-sm font-medium text-gray-700">Active Status:</label>
              <select
                value={activeStatus}
                onChange={(e) => handleActiveStatusChange(e.target.value)}
                className="border px-3 py-1 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white"
              >
                <option value="Active">Active</option>
                <option value="Break">Break</option>
              </select>
              {isOnBreak && (
                <div className="flex items-center gap-2 text-sm">
                  <Clock className="w-4 h-4 text-orange-500" />
                  <span className="text-orange-600 font-medium">{breakTimer}</span>
                </div>
              )}
            </div>
          )}
          
          {/* Search - Only for Admin */}
          {userRoleIsAdmin && (
            <input
              type="text"
              placeholder="Search agents..."
              value={agentSearch}
              onChange={e => setAgentSearch(e.target.value)}
              className="border px-3 py-1 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          )}
          
          <div className="relative">
            <button
              onClick={() => setShowAgentExport(v => !v)}
              className="flex items-center bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
            >
              <Download className="w-4 h-4 mr-1" /> Export
            </button>
            {showAgentExport && (
              <div className="absolute right-0 mt-2 w-32 bg-white border rounded shadow z-10">
                <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToExcel(displayAgents, 'agents'); setShowAgentExport(false); }}>Excel</button>
                <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToPDF(displayAgents, 'agents', ['agent_number','name','email','status','is_admin']); setShowAgentExport(false); }}>PDF</button>
                <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToCSV(displayAgents, 'agents'); setShowAgentExport(false); }}>CSV</button>
                <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToXML(displayAgents, 'agents'); setShowAgentExport(false); }}>XML</button>
              </div>
            )}
          </div>
          
          {/* Add Agent Button - Only for Admin */}
          {userRoleIsAdmin && (
            <button
              onClick={() => {
                setShowAddModal(true);
                setFormData({
                  agent_number: '',
                  name: '',
                  email: '',
                  password: '',
                  status: 'Active',
                  is_admin: false
                });
                setError('');
              }}
              className="flex items-center bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
            >
              <Plus className="w-4 h-4 mr-1" /> Add Agent
            </button>
          )}
        </div>
      </div>
      
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Agent Number</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
              {/* Password column - Only for Admin */}
              {userRoleIsAdmin && (
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Password</th>
              )}
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
              {/* Active Status column - Only for Non-Admin */}
              {!userRoleIsAdmin && (
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Active Status</th>
              )}
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Admin</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {displayAgents.map((agent) => (
              <tr key={agent.id}>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{agent.agent_number}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{agent.name}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{agent.email}</td>
                {/* Password - Only for Admin */}
                {userRoleIsAdmin && (
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{agent.password}</td>
                )}
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={agentStatusClass(agent.status)}>{agent.status}</span>
                </td>
                {/* Active Status - Only for Non-Admin and Current User */}
                {!userRoleIsAdmin && agent.agent_number === user?.agent_number && (
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center gap-2">
                      <span className={`px-2 py-1 text-xs rounded-full font-medium ${
                        activeStatus === 'Active' 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-orange-100 text-orange-800'
                      }`}>
                        {activeStatus}
                      </span>
                      {isOnBreak && (
                        <span className="text-xs text-gray-500">({breakTimer})</span>
                      )}
                    </div>
                  </td>
                )}
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  {agent.is_admin ? (
                    <span className="text-blue-600 font-medium">Yes</span>
                  ) : (
                    <span className="text-gray-400">No</span>
                  )}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium space-x-2">
                  {/* Edit button - Always visible for own profile or admin */}
                  <button
                    onClick={() => openEditModal(agent)}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    <Edit className="inline w-4 h-4" />
                  </button>
                  {/* Delete button - Only for Admin */}
                  {userRoleIsAdmin && (
                    <button
                      onClick={() => handleDeleteAgent(agent.agent_number)}
                      className="text-red-500 hover:text-red-700"
                      disabled={deleteLoading}
                    >
                      <Trash2 className="inline w-4 h-4" />
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Break Modal */}
      {showBreakModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md relative">
            <button 
              className="absolute top-2 right-2 text-gray-400 hover:text-gray-600" 
              onClick={() => {
                setShowBreakModal(false);
                setBreakData({ fromTime: '', toTime: '', remark: '' });
              }}
            >
              &times;
            </button>
            <h3 className="text-lg font-bold mb-4 flex items-center gap-2">
              <Pause className="w-5 h-5 text-orange-500" />
              Set Break Time
            </h3>
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-1">From Time</label>
                  <input
                    type="time"
                    value={breakData.fromTime}
                    onChange={(e) => setBreakData({ ...breakData, fromTime: e.target.value })}
                    className="w-full border px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                  {breakData.fromTime && !isValidFromTime(breakData.fromTime) && (
                    <p className="text-red-500 text-xs mt-1">Cannot select past time</p>
                  )}
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">To Time</label>
                  <input
                    type="time"
                    value={breakData.toTime}
                    onChange={(e) => setBreakData({ ...breakData, toTime: e.target.value })}
                    className="w-full border px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Remark</label>
                <input
                  type="text"
                  placeholder="e.g., Lunch, Team Meeting, Personal Break"
                  value={breakData.remark}
                  onChange={(e) => setBreakData({ ...breakData, remark: e.target.value })}
                  className="w-full border px-3 py-2 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <div className="flex gap-2 pt-2">
                <button
                  onClick={() => {
                    setShowBreakModal(false);
                    setBreakData({ fromTime: '', toTime: '', remark: '' });
                  }}
                  className="flex-1 bg-gray-500 text-white py-2 rounded hover:bg-gray-600"
                >
                  Cancel
                </button>
                <button
                  onClick={startBreak}
                  className="flex-1 bg-orange-500 text-white py-2 rounded hover:bg-orange-600 flex items-center justify-center gap-2"
                  disabled={!breakData.fromTime || !breakData.toTime || !breakData.remark || !isValidFromTime(breakData.fromTime)}
                >
                  <Play className="w-4 h-4" />
                  Start Break
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Add Agent Modal - Only for Admin */}
      {showAddModal && userRoleIsAdmin && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-8 w-full max-w-md relative">
            <button className="absolute top-2 right-2 text-gray-400 hover:text-gray-600" onClick={() => setShowAddModal(false)}>&times;</button>
            <h3 className="text-lg font-bold mb-4">Add Agent</h3>
            <form className="space-y-4" onSubmit={handleAddAgent}>
              <div>
                <label className="block text-sm font-medium mb-1">Agent Number</label>
                <input type="text" className="w-full border px-3 py-2 rounded" value={formData.agent_number} onChange={e => setFormData({ ...formData, agent_number: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Name</label>
                <input type="text" className="w-full border px-3 py-2 rounded" value={formData.name} onChange={e => setFormData({ ...formData, name: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Email</label>
                <input type="email" className="w-full border px-3 py-2 rounded" value={formData.email} onChange={e => setFormData({ ...formData, email: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Password</label>
                <input type="text" className="w-full border px-3 py-2 rounded" value={formData.password} onChange={e => setFormData({ ...formData, password: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Status</label>
                <select className="w-full border px-3 py-2 rounded" value={formData.status} onChange={e => setFormData({ ...formData, status: e.target.value })}>
                  <option value="Active">Active</option>
                  <option value="Inactive">Inactive</option>
                </select>
              </div>
              <div className="flex items-center">
                <input type="checkbox" id="is_admin_add" checked={formData.is_admin} onChange={e => setFormData({ ...formData, is_admin: e.target.checked })} />
                <label htmlFor="is_admin_add" className="ml-2 text-sm">Admin</label>
              </div>
              {error && <div className="text-red-500 text-sm">{error}</div>}
              <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600" disabled={loading}>{loading ? 'Adding...' : 'Add Agent'}</button>
            </form>
          </div>
        </div>
      )}
      
      {/* Edit Agent Modal */}
      {showEditModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-8 w-full max-w-md relative">
            <button className="absolute top-2 right-2 text-gray-400 hover:text-gray-600" onClick={() => setShowEditModal(false)}>&times;</button>
            <h3 className="text-lg font-bold mb-4">Edit Agent</h3>
            <form className="space-y-4" onSubmit={handleEditAgent}>
              <div>
                <label className="block text-sm font-medium mb-1">Agent Number</label>
                <input type="text" className="w-full border px-3 py-2 rounded bg-gray-100" value={formData.agent_number} disabled />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Name</label>
                <input type="text" className="w-full border px-3 py-2 rounded" value={formData.name} onChange={e => setFormData({ ...formData, name: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Email</label>
                <input type="email" className="w-full border px-3 py-2 rounded" value={formData.email} onChange={e => setFormData({ ...formData, email: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Password (leave blank to keep unchanged)</label>
                <input type="text" className="w-full border px-3 py-2 rounded" value={formData.password} onChange={e => setFormData({ ...formData, password: e.target.value })} />
              </div>
              {/* Admin-only fields in edit modal */}
              {userRoleIsAdmin && (
                <>
                  <div>
                    <label className="block text-sm font-medium mb-1">Status</label>
                    <select className="w-full border px-3 py-2 rounded" value={formData.status} onChange={e => setFormData({ ...formData, status: e.target.value })}>
                      <option value="Active">Active</option>
                      <option value="Inactive">Inactive</option>
                    </select>
                  </div>
                  <div className="flex items-center">
                    <input type="checkbox" id="is_admin_edit" checked={formData.is_admin} onChange={e => setFormData({ ...formData, is_admin: e.target.checked })} />
                    <label htmlFor="is_admin_edit" className="ml-2 text-sm">Admin</label>
                  </div>
                </>
              )}
              {error && <div className="text-red-500 text-sm">{error}</div>}
              <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600" disabled={loading}>{loading ? 'Saving...' : 'Save Changes'}</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AgentsTable;
