import React, { useState, useEffect } from 'react';
import { User, Phone, LogOut, Users, Edit, Trash2, Plus, Download } from 'lucide-react';
import * as XLSX from 'xlsx';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { saveAs } from 'file-saver';
import { js2xml } from 'xml-js';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import AddAgentModal from './components/Modals/AddAgentModal';
import EditAgentModal from './components/Modals/EditAgentModal';
import AltNumbersModal from './components/Modals/AltNumbersModal';
import ViewAllCallsModal from './components/Modals/ViewAllCallsModal';
import { exportToExcel, exportToCSV, exportToPDF, exportToXML } from './utils/exportHelpers';
import { formatDuration, callStatusClass, agentStatusClass } from './utils/format';
import {
  fetchCallDetails,
  handleLogin as apiHandleLogin,
  handleUpdateCall as apiHandleUpdateCall,
  handleSaveAltNumbers as apiHandleSaveAltNumbers,
  handleSaveViewAllRemarks as apiHandleSaveViewAllRemarks
} from './utils/api';
import useAuth from './hooks/useAuth';
import useCalls from './hooks/useCalls';
import useAgents from './hooks/useAgents';


const App = () => {
  const {
    currentScreen,
    setCurrentScreen,
    user,
    setUser,
    loginData,
    setLoginData,
    handleLogin,
    handleLogout,
    loading,
    error,
    setError
  } = useAuth();
  const [activeTab, setActiveTab] = useState('calls');
  const {
    agentDetails,
    setAgentDetails,
    fetchAgentDetails,
    handleAddAgent,
    handleEditAgent,
    handleDeleteAgent,
    deleteLoading,
    setDeleteLoading
  } = useAgents();
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    agent_number: '',
    name: '',
    email: '',
    password: '',
    status: 'Active',
    is_admin: false,
  });
  const [showAltNumbersModal, setShowAltNumbersModal] = useState(false);
  const [selectedCall, setSelectedCall] = useState(null);
  const [altNumbersInput, setAltNumbersInput] = useState('');
  const [altNumbersLoading, setAltNumbersLoading] = useState(false);
  const [viewAllRemarksEdit, setViewAllRemarksEdit] = useState(false);
  const [viewAllRemarksInput, setViewAllRemarksInput] = useState('');
  const [viewAllRemarksLoading, setViewAllRemarksLoading] = useState(false);
  const [fromDate, setFromDate] = useState('');
  const [toDate, setToDate] = useState('');
  const [manualCallNumber, setManualCallNumber] = useState('');

  const API_BASE = 'http://localhost:5000/api';

  // Load token and user from localStorage on mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setUser(JSON.parse(userData));
      setCurrentScreen('dashboard');
      fetchCallDetails(token);
      fetchAgentDetails(token);
    }
  }, []);

  const getLatestCallsByCustomer = (calls) => {
    const grouped = {};

    calls.forEach(call => {
      const existing = grouped[call.customer_number];
      if (!existing || new Date(call.timestamp) > new Date(existing.timestamp)) {
        grouped[call.customer_number] = call;
      }
    });

    return Object.values(grouped);
  };

  const [viewCustomer, setViewCustomer] = useState(null);
  const [showCallModal, setShowCallModal] = useState(false);

  const handleViewCustomerCalls = (customerNumber) => {
    const customerCalls = callDetails.filter(c => c.customer_number === customerNumber);
    setViewCustomer({ number: customerNumber, calls: customerCalls });
    setShowCallModal(true);
  };

  // Handler for Edit button
  const handleEditCall = (call) => {
    setEditingCallId(call.id);
    setEditInputs({ remarks: '', name: '', remarks_status: '', recordings: '', alternative_numbers: '' }); // Always empty on edit
  };

  // Handler for Cancel button
  const handleCancelEdit = () => {
    setEditingCallId(null);
    setEditInputs({ remarks: '', name: '', remarks_status: '', recordings: '', alternative_numbers: '' });
  };

  // Handler for Update button
  const handleUpdateCall = async (callId) => {
    setEditLoading(true);
    try {
      const response = await apiHandleUpdateCall(callId, editInputs);
      if (response.ok) {
        await fetchCallDetails(localStorage.getItem('token'));
        setEditingCallId(null);
        setEditInputs({ remarks: '', name: '', remarks_status: '', recordings: '', alternative_numbers: '' });
      } else {
        // Optionally handle error
      }
    } catch (err) {
      // Optionally handle error
    } finally {
      setEditLoading(false);
    }
  };

  // Filtered data for calls and agents
  const filteredCalls = callDetails.filter(call => {
    const search = callSearch.toLowerCase();
    return (
      call.agent_number?.toLowerCase().includes(search) ||
      call.customer_number?.toLowerCase().includes(search) ||
      call.call_status?.toLowerCase().includes(search) ||
      call.timestamp?.toLowerCase().includes(search)
    );
  });

  const filteredAgents = (user?.role === 'admin'
    ? agentDetails
    : agentDetails.filter(agent => agent.agent_number === user?.agent_number)
  ).filter(agent => {
    const search = agentSearch.toLowerCase();
    return (
      agent.agent_number?.toLowerCase().includes(search) ||
      agent.name?.toLowerCase().includes(search) ||
      agent.email?.toLowerCase().includes(search) ||
      (agent.status || '').toLowerCase().includes(search)
    );
  });

  // Add handler for date search
  const handleDateSearch = () => {
    const token = localStorage.getItem('token');
    fetchCallDetails(token, fromDate, toDate);
  };

  // Add handler for manual call
  const handleManualCall = () => {
    if (!manualCallNumber.trim()) {
      alert('Please enter a phone number');
      return;
    }
    // Placeholder function - no actual call integration
    alert(`Calling ${manualCallNumber}... (This is a placeholder - no actual call will be made)`);
    console.log('Manual call initiated for:', manualCallNumber);
  };

  // Add handler for alternative numbers
  const handleOpenAltNumbersModal = (call) => {
    setSelectedCall(call);
    setAltNumbersInput(call.alternative_numbers || '');
    setShowAltNumbersModal(true);
  };

  const handleSaveAltNumbers = async () => {
    if (!selectedCall) return;

    setAltNumbersLoading(true);
    try {
      const response = await apiHandleSaveAltNumbers(selectedCall.id, altNumbersInput);

      if (response.ok) {
        await fetchCallDetails(localStorage.getItem('token'));
        setShowAltNumbersModal(false);
        setSelectedCall(null);
        setAltNumbersInput('');
      } else {
        alert('Failed to save alternative numbers');
      }
    } catch (err) {
      alert('Error saving alternative numbers');
    } finally {
      setAltNumbersLoading(false);
    }
  };

  // Add handler for View All modal remarks editing
  const handleViewAllRemarksEdit = () => {
    if (!viewCustomer?.calls || viewCustomer.calls.length === 0) return;

    // Get the most recent call's remarks as the customer remarks
    const mostRecentCall = viewCustomer.calls[0];
    setViewAllRemarksInput(mostRecentCall.remarks || '');
    setViewAllRemarksEdit(true);
  };

  const handleSaveViewAllRemarks = async () => {
    if (!viewCustomer?.calls || viewCustomer.calls.length === 0) return;

    setViewAllRemarksLoading(true);
    try {
      const mostRecentCall = viewCustomer.calls[0];
      const response = await apiHandleSaveViewAllRemarks(mostRecentCall.id, viewAllRemarksInput);

      if (response.ok) {
        await fetchCallDetails(localStorage.getItem('token'));
        setViewAllRemarksEdit(false);
        setViewAllRemarksInput('');
      } else {
        alert('Failed to save remarks');
      }
    } catch (err) {
      alert('Error saving remarks');
    } finally {
      setViewAllRemarksLoading(false);
    }
  };

  // Login Screen
  if (currentScreen === 'login') {
    return (
      <Login
        loginData={loginData}
        setLoginData={setLoginData}
        handleLogin={handleLogin}
        loading={loading}
        error={error}
      />
    );
  }

  // Dashboard Screen
  const token = localStorage.getItem('token');

  // Tab button class helper
  const tabClass = (tab) =>
    `py-4 px-1 border-b-2 font-medium text-sm ${
      activeTab === tab
        ? 'border-blue-500 text-blue-600'
        : 'border-transparent text-gray-500 hover:text-gray-700'
    }`;

  if (currentScreen === 'dashboard') {
    return (
      <Dashboard
        user={user}
        handleLogout={handleLogout}
        activeTab={activeTab}
        setActiveTab={setActiveTab}
        tabClass={tabClass}
        callSearch={callSearch}
        setCallSearch={setCallSearch}
        agentSearch={agentSearch}
        setAgentSearch={setAgentSearch}
        fromDate={fromDate}
        setFromDate={setFromDate}
        toDate={toDate}
        setToDate={setToDate}
        handleDateSearch={handleDateSearch}
        showCallExport={showCallExport}
        setShowCallExport={setShowCallExport}
        showAgentExport={showAgentExport}
        setShowAgentExport={setShowAgentExport}
        filteredCalls={filteredCalls}
        filteredAgents={filteredAgents}
        exportToExcel={exportToExcel}
        exportToPDF={exportToPDF}
        exportToCSV={exportToCSV}
        exportToXML={exportToXML}
        setShowAddModal={setShowAddModal}
        setFormData={setFormData}
        setError={setError}
        userRoleIsAdmin={user?.role === 'admin'}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <h1 className="text-xl font-semibold text-gray-900">Call Center Dashboard</h1>
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-600">Welcome, {user?.name || 'User'}</span>
              <button
                onClick={handleLogout}
                className="flex items-center space-x-1 text-gray-600 hover:text-gray-900"
              >
                <LogOut size={16} />
                <span>Logout</span>
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation Tabs */}
      <nav className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex space-x-8">
            <button
              onClick={() => setActiveTab('calls')}
              className={tabClass('calls')}
            >
              <Phone className="inline-block w-4 h-4 mr-2" />
              Call Details
            </button>
            <button
              onClick={() => setActiveTab('missed')}
              className={tabClass('missed')}
            >
              <Phone className="inline-block w-4 h-4 mr-2 text-red-500" />
              Missed Calls
            </button>
            <button
              onClick={() => setActiveTab('agents')}
              className={tabClass('agents')}
            >
              <Users className="inline-block w-4 h-4 mr-2" />
              Agent Details
            </button>
            <button
              onClick={() => setActiveTab('manual-call')}
              className={tabClass('manual-call')}
            >
              <Phone className="inline-block w-4 h-4 mr-2" />
              Manual Call
            </button>
          </div>
        </div>
      </nav>

      {/* Content */}
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        {activeTab === 'calls' && (
          <div className="bg-white shadow rounded-lg">
            <div className="px-6 py-4 border-b border-gray-200 flex flex-col sm:flex-row sm:items-center sm:justify-between">
              <h2 className="text-lg font-medium text-gray-900">Call Details Report</h2>
              <div className="flex flex-row items-center gap-2 mt-2 sm:mt-0">
                <label className="text-xs font-medium text-gray-500">From</label>
                <input
                  type="date"
                  value={fromDate}
                  onChange={e => setFromDate(e.target.value)}
                  className="border px-2 py-1 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 text-xs"
                  style={{ width: 120 }}
                  title="From date"
                />
                <label className="text-xs font-medium text-gray-500 ml-2">To</label>
                <input
                  type="date"
                  value={toDate}
                  onChange={e => setToDate(e.target.value)}
                  className="border px-2 py-1 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 text-xs"
                  style={{ width: 120 }}
                  title="To date"
                />
                <button
                  className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600 ml-2"
                  onClick={handleDateSearch}
                  style={{ minWidth: 90 }}
                >
                  Search
                </button>
                <input
                  type="text"
                  placeholder="Search calls..."
                  value={callSearch}
                  onChange={e => setCallSearch(e.target.value)}
                  className="border px-3 py-1 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 ml-4"
                  style={{ width: 180 }}
                />
                <div className="relative ml-2">
                  <button
                    onClick={() => setShowCallExport(v => !v)}
                    className="flex items-center bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                    style={{ minWidth: 90 }}
                  >
                    <Download className="w-4 h-4 mr-1" /> Export
                  </button>
                  {showCallExport && (
                    <div className="absolute right-0 mt-2 w-32 bg-white border rounded shadow z-10">
                      <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToExcel(filteredCalls, 'calls'); setShowCallExport(false); }}>Excel</button>
                      <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToPDF(filteredCalls, 'calls', ['agent_number','customer_number','duration','call_status','timestamp']); setShowCallExport(false); }}>PDF</button>
                      <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToCSV(filteredCalls, 'calls'); setShowCallExport(false); }}>CSV</button>
                      <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToXML(filteredCalls, 'calls'); setShowCallExport(false); }}>XML</button>
                    </div>
                  )}
                </div>
              </div>
            </div>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">

<thead className="bg-gray-50">
  <tr>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Customer Number</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Time</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Call Status</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Duration</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Agent Number</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Remarks Status</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Recordings</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Alt Numbers</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Click to Call</th>
    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
  </tr>
</thead>
<tbody className="bg-white divide-y divide-gray-200">
{getLatestCallsByCustomer(filteredCalls.filter(call => call.call_status === 'Completed')).map((call) => {
  const dateObj = new Date(call.timestamp);
  const date = dateObj.toLocaleDateString();
  const time = dateObj.toLocaleTimeString();
  const isEditing = editingCallId === call.id;
  return (
    <tr key={call.id}>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{call.customer_number}</td>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{date}</td>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{time}</td>
      <td className="px-6 py-4 whitespace-nowrap">
        <span className={callStatusClass(call.call_status)}>{call.call_status}</span>
      </td>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{formatDuration(call.duration)}</td>
      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{call.agent_number}</td>
      {/* Name column - value only */}
      <td className="px-6 py-2 whitespace-normal break-words text-sm align-top text-left" style={{ minWidth: 180, maxWidth: 220 }}>
        {isEditing ? (
          <textarea
            className="border px-2 py-1 rounded"
            style={{ width: '100%', minHeight: 48, resize: 'vertical' }}
            value={editInputs.name}
            onChange={e => setEditInputs({ ...editInputs, name: e.target.value })}
            placeholder="Enter name"
            rows={3}
          />
        ) : (
          <div style={{ minHeight: 48, wordBreak: 'break-word' }}>
            {(call.name || '').replace(/<span.*?>.*?<\/span>/g, '').replace(/\|$/, '').trim()}
          </div>
        )}
      </td>
      {/* Remarks Status column - value only */}
      <td className="px-6 py-2 whitespace-normal break-words text-sm align-top text-left" style={{ minWidth: 180, maxWidth: 220 }}>
        {isEditing ? (
          <textarea
            className="border px-2 py-1 rounded"
            style={{ width: '100%', minHeight: 48, resize: 'vertical' }}
            value={editInputs.remarks_status}
            onChange={e => setEditInputs({ ...editInputs, remarks_status: e.target.value })}
            placeholder="Enter remarks status"
            rows={3}
          />
        ) : (
          <div style={{ minHeight: 48, wordBreak: 'break-word' }}>
            {(call.remarks_status || '').replace(/<span.*?>.*?<\/span>/g, '').replace(/\|$/, '').trim()}
          </div>
        )}
      </td>

      <td className="px-6 py-4 text-sm">
        {isEditing ? (
          <input
            type="text"
            className="border px-2 py-1 rounded text-xs w-full"
            value={editInputs.recordings}
            onChange={e => setEditInputs({ ...editInputs, recordings: e.target.value })}
            placeholder="Enter recording URLs (comma-separated)"
          />
        ) : (
          call.recordings ? (
            <div className="space-y-1">
              {call.recordings.split(',').map((recording, index) => (
                <div key={index} className="flex items-center space-x-2">
                  <button 
                    className="bg-blue-500 text-white px-2 py-1 rounded text-xs hover:bg-blue-600"
                    onClick={() => window.open(recording.trim(), '_blank')}
                  >
                    <span className="mr-1"></span>Play
                  </button>
                  <span className="text-xs text-gray-600">Recording {index + 1}</span>
                </div>
              ))}
            </div>
          ) : (
            <span className="text-gray-400 text-xs">No recordings</span>
          )
        )}
      </td>
      <td className="px-6 py-4 text-sm">
        <div className="flex items-center space-x-2">
          {call.alternative_numbers ? (
            <div className="flex items-center space-x-1">
              <span className="text-xs text-gray-600 bg-gray-100 px-2 py-1 rounded">
                {call.alternative_numbers.split(',').length} alt
              </span>
              <button
                onClick={() => handleOpenAltNumbersModal(call)}
                className="text-blue-500 hover:text-blue-700 text-xs"
                title="View/Edit Alternative Numbers"
              >
                <span className="underline">View</span>
              </button>
            </div>
          ) : (
            <button
              onClick={() => handleOpenAltNumbersModal(call)}
              className="text-blue-500 hover:text-blue-700 text-xs"
              title="Add Alternative Numbers"
            >
              <span className="underline">Add</span>
            </button>
          )}
        </div>
      </td>
      <td className="px-6 py-4 whitespace-nowrap text-sm">
      <a
          href={`sip:${call.customer_number}@192.168.31.22`}
          className="ml-2 text-green-600 hover:underline"
          style={{ marginLeft: 8 }}
        >
            <Phone size={16} />
        </a>
      </td>
      <td className="px-6 py-4 whitespace-nowrap text-sm">    
      <button
          className="text-blue-500 hover:underline mr-2"
          onClick={() => handleViewCustomerCalls(call.customer_number)}
        >
          View All
        </button>
        {isEditing ? (
          <>
            <button
              className="text-green-600 hover:underline mr-2"
              onClick={() => handleUpdateCall(call.id)}
              disabled={editLoading}
            >
              {editLoading ? 'Updating...' : 'Update'}
            </button>
            <button
              className="text-gray-500 hover:underline"
              onClick={handleCancelEdit}
            >
              Cancel
            </button>
          </>
        ) : (
          <>
            <button
              className="text-orange-500 hover:underline"
              onClick={() => handleEditCall(call)}
            >
              Edit
            </button>
          </>
                            )}
                  </td>
                  <td className="px-6 py-4 text-sm">
                    {call.alternative_numbers ? (
                      <div className="text-xs text-gray-600">
                        {call.alternative_numbers.split(',').map((num, index) => (
                          <div key={index} className="mb-1">
                            {num.trim()}
                          </div>
                        ))}
                      </div>
                    ) : (
                      <span className="text-gray-400 text-xs">None</span>
                    )}
                  </td>
                </tr>
              );
            })}
</tbody>

              </table>
            </div>
          </div>
        )}

        {activeTab === 'missed' && (
          <div className="bg-white shadow rounded-lg">
            <div className="px-6 py-4 border-b border-gray-200 flex flex-col sm:flex-row sm:items-center sm:justify-between">
              <h2 className="text-lg font-medium text-gray-900">Missed Calls Report</h2>
            </div>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Customer Number</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Time</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Call Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Duration</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Agent Number</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Remarks Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Recordings</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Alt Numbers</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Click to Call</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {getLatestCallsByCustomer(callDetails.filter(call => call.call_status === 'Missed' || call.call_status === 'Failed')).map((call) => {
                    const dateObj = new Date(call.timestamp);
                    const date = dateObj.toLocaleDateString();
                    const time = dateObj.toLocaleTimeString();
                    const isEditing = editingCallId === call.id;
                    return (
                      <tr key={call.id}>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{call.customer_number}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{date}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{time}</td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={callStatusClass(call.call_status)}>{call.call_status}</span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{formatDuration(call.duration)}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{call.agent_number}</td>
                        {/* Name column - value only */}
                        <td className="px-6 py-2 whitespace-normal break-words text-sm align-top text-left"style={{width: 220, maxWidth: 220, minWidth: 180, position: 'relative' }}>
                          {isEditing ? (
                            <textarea
                              className="border px-2 py-1 rounded"
                              style={{ width: '100%', minHeight: 48, resize: 'vertical' }}
                              value={editInputs.name}
                              onChange={e => setEditInputs({ ...editInputs, name: e.target.value })}
                              placeholder="Enter name"
                              rows={3}
                            />
                          ) : (
                            <div style={{ minHeight: 48, wordBreak: 'break-word' }}>
                              {(call.name || '').replace(/<span.*?>.*?<\/span>/g, '').replace(/\|$/, '').trim()}
                            </div>
                          )}
                        </td>
                        {/* Remarks Status column - value only */}
                        <td className="px-6 py-2 whitespace-normal break-words text-sm align-top text-left" style={{width: 220, maxWidth: 220, minWidth: 180, position: 'relative' }}>
                          {isEditing ? (
                            <textarea
                              className="border px-2 py-1 rounded"
                              style={{ width: '100%', minHeight: 48, resize: 'vertical' }}
                              value={editInputs.remarks_status}
                              onChange={e => setEditInputs({ ...editInputs, remarks_status: e.target.value })}
                              placeholder="Enter remarks status"
                              rows={3}
                            />
                          ) : (
                            <div style={{ minHeight: 48, wordBreak: 'break-word' }}>
                              {(call.remarks_status || '').replace(/<span.*?>.*?<\/span>/g, '').replace(/\|$/, '').trim()}
                            </div>
                          )}
                        </td>

                        <td className="px-6 py-4 text-sm">
                          {isEditing ? (
                            <input
                              type="text"
                              className="border px-2 py-1 rounded text-xs w-full"
                              value={editInputs.recordings}
                              onChange={e => setEditInputs({ ...editInputs, recordings: e.target.value })}
                              placeholder="Enter recording URLs (comma-separated)"
                            />
                          ) : (
                            call.recordings ? (
                              <div className="space-y-1">
                                {call.recordings.split(',').map((recording, index) => (
                                  <div key={index} className="flex items-center space-x-2">
                                    <button 
                                      className="bg-blue-500 text-white px-2 py-1 rounded text-xs hover:bg-blue-600"
                                      onClick={() => window.open(recording.trim(), '_blank')}
                                    >
                                      <span className="mr-1"></span>Play
                                    </button>
                                    <span className="text-xs text-gray-600">Recording {index + 1}</span>
                                  </div>
                                ))}
                              </div>
                            ) : (
                              <span className="text-gray-400 text-xs">No recordings</span>
                            )
                          )}
                        </td>
                        <td className="px-6 py-4 text-sm" style={{width: 220, maxWidth: 220, minWidth: 180, position: 'relative' }}>
                          <div className="flex items-center space-x-2">
                            {call.alternative_numbers ? (
                              <div className="flex items-center space-x-1">
                                <span className="text-xs text-gray-600 bg-gray-100 px-2 py-1 rounded">
                                  {call.alternative_numbers.split(',').length} alt
                                </span>
                                <button
                                  onClick={() => handleOpenAltNumbersModal(call)}
                                  className="text-blue-500 hover:text-blue-700 text-xs"
                                  title="View/Edit Alternative Numbers"
                                >
                                  <span className="underline">View</span>
                                </button>
                              </div>
                            ) : (
                              <button
                                onClick={() => handleOpenAltNumbersModal(call)}
                                className="text-blue-500 hover:text-blue-700 text-xs"
                                title="Add Alternative Numbers"
                              >
                                <span className="underline">Add</span>
                              </button>
                            )}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm">
                        <a
                            href={`sip:${call.customer_number}@192.168.31.22`}
                            className="ml-2 text-green-600 hover:underline"
                            style={{ marginLeft: 8 }}
                          >
                             <Phone size={16} />
                          </a>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm">
                        <div className="flex gap-2">

                          {isEditing ? (
                            <>
                              <button
                                className="text-green-600 hover:underline mr-2"
                                onClick={() => handleUpdateCall(call.id)}
                                disabled={editLoading}
                              >
                                {editLoading ? 'Updating...' : 'Update'}
                              </button>
                              <button
                                className="text-gray-500 hover:underline"
                                onClick={handleCancelEdit}
                              >
                                Cancel
                              </button>
                            </>
                          ) : (
                            <>
                              <button
                                className="text-orange-500 hover:underline"
                                onClick={() => handleEditCall(call)}
                              >
                                Edit
                              </button>
                            </>
                          )}
                          </div>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {activeTab === 'agents' && (
          <div className="bg-white shadow rounded-lg">
            <div className="px-6 py-4 border-b border-gray-200 flex flex-col sm:flex-row sm:items-center sm:justify-between">
              <h2 className="text-lg font-medium text-gray-900">Agent Details</h2>
              <div className="flex flex-col sm:flex-row sm:items-center gap-2 mt-2 sm:mt-0">
                <input
                  type="text"
                  placeholder="Search agents..."
                  value={agentSearch}
                  onChange={e => setAgentSearch(e.target.value)}
                  className="border px-3 py-1 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <div className="relative">
                  <button
                    onClick={() => setShowAgentExport(v => !v)}
                    className="flex items-center bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                  >
                    <Download className="w-4 h-4 mr-1" /> Export
                  </button>
                  {showAgentExport && (
                    <div className="absolute right-0 mt-2 w-32 bg-white border rounded shadow z-10">
                      <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToExcel(filteredAgents, 'agents'); setShowAgentExport(false); }}>Excel</button>
                      <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToPDF(filteredAgents, 'agents', ['agent_number','name','email','status','is_admin']); setShowAgentExport(false); }}>PDF</button>
                      <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToCSV(filteredAgents, 'agents'); setShowAgentExport(false); }}>CSV</button>
                      <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToXML(filteredAgents, 'agents'); setShowAgentExport(false); }}>XML</button>
                    </div>
                  )}
                </div>
                {user?.role === 'admin' && (
                  <button
                    onClick={() => { setShowAddModal(true); setFormData({ agent_number: '', name: '', email: '', password: '', status: 'Active', is_admin: false }); setError(''); }}
                    className="flex items-center bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                  >
                    <Plus className="w-4 h-4 mr-1" /> Add Agent
                  </button>
                )}
              </div>
            </div>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Agent Number</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Password</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Admin</th>
                    {user?.role === 'admin' && <th className="px-6 py-3"></th>}
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredAgents.map((agent) => (
                    <tr key={agent.id}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{agent.agent_number}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{agent.name}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{agent.email}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{agent.password}</td>
                      <td className="px-6 py-4 whitespace-nowrap"><span className={agentStatusClass(agent.status)}>{agent.status}</span></td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{agent.is_admin ? (<span className="text-blue-600 font-medium">Yes</span>) : (<span className="text-gray-400">No</span>)}</td>
                      {user?.role === 'admin' && (
                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium space-x-2">
                          <button onClick={() => openEditModal(agent)} className="text-blue-500 hover:text-blue-700"><Edit className="inline w-4 h-4" /></button>
                          <button onClick={() => handleDeleteAgent(agent.agent_number)} className="text-red-500 hover:text-red-700" disabled={deleteLoading}><Trash2 className="inline w-4 h-4" /></button>
                        </td>
                      )}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {activeTab === 'manual-call' && (
          <div className="bg-white shadow rounded-lg">
            <div className="px-6 py-4 border-b border-gray-200">
              <h2 className="text-lg font-medium text-gray-900">Manual Call</h2>
            </div>
            <div className="p-6">
              <div className="max-w-md mx-auto">
                <div className="space-y-4">
                  <div>
                    <label htmlFor="phoneNumber" className="block text-sm font-medium text-gray-700 mb-2">
                      Phone Number
                    </label>
                    <input
                      type="tel"
                      id="phoneNumber"
                      value={manualCallNumber}
                      onChange={(e) => setManualCallNumber(e.target.value)}
                      placeholder="Enter phone number (e.g., +1234567890)"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                  </div>
                  <div className="flex justify-center">
                    <button
                      onClick={handleManualCall}
                      className="flex items-center space-x-2 bg-green-500 text-white px-6 py-3 rounded-md hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 transition-colors"
                    >
                      <Phone size={20} />
                      <span>Call Now</span>
                    </button>
                  </div>
                  <div className="text-center text-sm text-gray-500 mt-4">
                    <p>This feature allows you to initiate a call to any phone number.</p>
                    <p className="mt-1">Note: This is a placeholder interface - no actual calls will be made.</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
      <AddAgentModal
        show={showAddModal}
        onClose={() => setShowAddModal(false)}
        formData={formData}
        setFormData={setFormData}
        handleAddAgent={handleAddAgent}
        loading={loading}
        error={error}
      />
      <EditAgentModal
        show={showEditModal}
        onClose={() => setShowEditModal(false)}
        formData={formData}
        setFormData={setFormData}
        handleEditAgent={handleEditAgent}
        loading={loading}
        error={error}
      />
      <AltNumbersModal
        show={showAltNumbersModal}
        onClose={() => setShowAltNumbersModal(false)}
        selectedCall={selectedCall}
        altNumbersInput={altNumbersInput}
        setAltNumbersInput={setAltNumbersInput}
        handleSaveAltNumbers={handleSaveAltNumbers}
        altNumbersLoading={altNumbersLoading}
      />
      <ViewAllCallsModal
        show={showCallModal}
        onClose={() => setShowCallModal(false)}
        viewCustomer={viewCustomer}
        viewAllRemarksEdit={viewAllRemarksEdit}
        viewAllRemarksInput={viewAllRemarksInput}
        setViewAllRemarksInput={setViewAllRemarksInput}
        handleViewAllRemarksEdit={handleViewAllRemarksEdit}
        handleSaveViewAllRemarks={handleSaveViewAllRemarks}
        viewAllRemarksLoading={viewAllRemarksLoading}
        setViewAllRemarksEdit={setViewAllRemarksEdit}
      />
    </div>

  );
};

export default App;
