import React, { useState, useEffect } from 'react';
import { Edit, Trash2, Plus, Download, Clock, Play, Pause, Search } from 'lucide-react';
import { agentStatusClass } from '../utils/format'; // adjust path if needed

const AgentsTable = ({
  user,
  agentSearch,
  setAgentSearch,
  showAgentExport,
  setShowAgentExport,
  filteredAgents,
  exportToExcel,
}) => {
  const [statusDropdown, setStatusDropdown] = useState(null);
  const [breakModalAgent, setBreakModalAgent] = useState(null);
  const [breakData, setBreakData] = useState({ fromTime: '', toTime: '', remark: '' });
  const [agentBreakHistory, setAgentBreakHistory] = useState({});

  const isValidFromTime = (fromTime) => {
    const now = new Date();
    const [hours, minutes] = fromTime.split(':').map(Number);
    const selected = new Date();
    selected.setHours(hours);
    selected.setMinutes(minutes);
    return selected > now;
  };

  const isValidTimeRange = () => {
    const [fromH, fromM] = breakData.fromTime.split(':').map(Number);
    const [toH, toM] = breakData.toTime.split(':').map(Number);
    const fromMinutes = fromH * 60 + fromM;
    const toMinutes = toH * 60 + toM;
    return toMinutes > fromMinutes;
  };

  const toggleDropdown = (index) => {
    setStatusDropdown(statusDropdown === index ? null : index);
  };

  const startBreak = () => {
    const agent = breakModalAgent;
    const record = {
      fromTime: breakData.fromTime,
      toTime: breakData.toTime,
      remark: breakData.remark,
      startTime: new Date(),
    };
    setAgentBreakHistory((prev) => ({
      ...prev,
      [agent.agent_number]: {
        ...record,
        lastBreakEnd: new Date(new Date().setHours(...breakData.toTime.split(':'))),
      },
    }));

    // Replace with your API call
    // await saveBreakRecord(record);

    setBreakModalAgent(null);
    setBreakData({ fromTime: '', toTime: '', remark: '' });
  };

  const closeModal = () => {
    setBreakModalAgent(null);
    setBreakData({ fromTime: '', toTime: '', remark: '' });
  };

  const isAdmin = user?.role === 'admin';
  const isAgent = user?.role === 'agent';

  const agentsToShow = isAdmin
    ? filteredAgents
    : filteredAgents.filter((agent) => agent.agent_number === user.agent_number);

  return (
    <div className="mt-4">
      <div className="flex justify-between items-center mb-4">
        {isAdmin && (
          <>
            <div className="relative w-1/3">
              <Search className="absolute left-2.5 top-2.5 h-4 w-4 text-muted-foreground" />
              <input
                className="w-full pl-8 pr-4 py-2 border rounded-md focus:outline-none"
                placeholder="Search Agent Name..."
                value={agentSearch}
                onChange={(e) => setAgentSearch(e.target.value)}
              />
            </div>
            <div className="space-x-2">
              <button
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                onClick={() => setShowAgentExport(!showAgentExport)}
              >
                <Download className="inline mr-1 w-4 h-4" /> Export
              </button>
              <button
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                <Plus className="inline mr-1 w-4 h-4" /> Add Agent
              </button>
            </div>
          </>
        )}
      </div>

      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-100">
          <tr>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Agent Name</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Mobile</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Status</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Last Break</th>
            {isAdmin && <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Actions</th>}
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {agentsToShow.map((agent, index) => (
            <tr key={index} className="hover:bg-gray-50">
              <td className="px-4 py-2 whitespace-nowrap">{agent.agent_name}</td>
              <td className="px-4 py-2 whitespace-nowrap">{agent.agent_mobile}</td>
              <td className="px-4 py-2 whitespace-nowrap">
                <div className="relative inline-block text-left">
                  <button
                    onClick={() => toggleDropdown(index)}
                    className={`inline-flex justify-center w-full px-4 py-2 text-sm font-medium border rounded-md focus:outline-none ${agentStatusClass(agent.status)}`}
                  >
                    {agent.status}
                  </button>
                  {statusDropdown === index && (
                    <div className="origin-top-right absolute right-0 mt-2 w-40 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5">
                      <div className="py-1">
                        {isAdmin && (
                          <>
                            <button className="w-full px-4 py-2 text-sm text-green-600 hover:bg-gray-100">Active</button>
                            <button className="w-full px-4 py-2 text-sm text-red-600 hover:bg-gray-100">Inactive</button>
                          </>
                        )}
                        {isAgent && (
                          <>
                            <button
                              className="w-full px-4 py-2 text-sm text-blue-600 hover:bg-gray-100"
                              onClick={() => setBreakModalAgent(agent)}
                            >
                              Break
                            </button>
                            <button className="w-full px-4 py-2 text-sm text-green-600 hover:bg-gray-100">Working</button>
                          </>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </td>
              <td className="px-4 py-2 whitespace-nowrap">
                {agentBreakHistory[agent.agent_number]?.remark && (
                  <>
                    <div className="text-xs text-gray-600">
                      {agentBreakHistory[agent.agent_number].remark}
                    </div>
                    <div className="text-xs text-gray-500">
                      Ends at: {agentBreakHistory[agent.agent_number].toTime}
                    </div>
                  </>
                )}
              </td>
              {isAdmin && (
                <td className="px-4 py-2 whitespace-nowrap space-x-2">
                  <button className="text-blue-500 hover:text-blue-700">
                    <Edit size={16} />
                  </button>
                  <button className="text-red-500 hover:text-red-700">
                    <Trash2 size={16} />
                  </button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>

      {breakModalAgent && (
        <div className="fixed inset-0 z-10 flex items-center justify-center bg-black bg-opacity-30">
          <div className="bg-white rounded-md shadow-md p-6 w-full max-w-md">
            <h2 className="text-lg font-semibold mb-4">Break Information</h2>
            <div className="mb-2">
              <label className="block text-sm font-medium text-gray-700">From Time</label>
              <input
                type="time"
                value={breakData.fromTime}
                onChange={(e) => setBreakData({ ...breakData, fromTime: e.target.value })}
                className="w-full px-3 py-2 border rounded-md"
              />
              {breakData.fromTime && !isValidFromTime(breakData.fromTime) && (
                <p className="text-red-500 text-xs mt-1">⚠️ From time must be in the future</p>
              )}
            </div>
            <div className="mb-2">
              <label className="block text-sm font-medium text-gray-700">To Time</label>
              <input
                type="time"
                value={breakData.toTime}
                onChange={(e) => setBreakData({ ...breakData, toTime: e.target.value })}
                className="w-full px-3 py-2 border rounded-md"
              />
              {breakData.toTime && !isValidTimeRange() && (
                <p className="text-red-500 text-xs mt-1">⚠️ To Time must be after From Time</p>
              )}
            </div>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700">Remark</label>
              <input
                type="text"
                value={breakData.remark}
                onChange={(e) => setBreakData({ ...breakData, remark: e.target.value })}
                className="w-full px-3 py-2 border rounded-md"
              />
            </div>
            <div className="flex justify-end space-x-2">
              <button
                onClick={closeModal}
                className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
              >
                Cancel
              </button>
              <button
                onClick={startBreak}
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                disabled={
                  !breakData.fromTime ||
                  !breakData.toTime ||
                  !breakData.remark ||
                  !isValidFromTime(breakData.fromTime) ||
                  !isValidTimeRange()
                }
              >
                Start Break
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AgentsTable;
