import React, { useState, useEffect } from 'react';
import { Edit, Trash2, Plus, Download, Clock, Play, Pause, Search } from 'lucide-react';
import { agentStatusClass } from '../utils/format'; // adjust path if needed

const AgentsTable = ({
  user,
  agentSearch,
  setAgentSearch,
  showAgentExport,
  setShowAgentExport,
  filteredAgents,
  exportToExcel,
  exportToPDF,
  exportToCSV,
  exportToXML,
  setShowAddModal,
  setFormData,
  setError,
  userRoleIsAdmin,
  openEditModal,
  handleDeleteAgent,
  deleteLoading,
  showAddModal,
  showEditModal,
  formData,
  setShowEditModal,
  handleAddAgent,
  handleEditAgent,
  loading,
  error
}) => {
  const [activeStatus, setActiveStatus] = useState('Active');
  const [breakData, setBreakData] = useState({
    fromTime: '',
    toTime: '',
    remark: ''
  });
  const [isOnBreak, setIsOnBreak] = useState(false);
  const [breakStartTime, setBreakStartTime] = useState(null);
  const [showBreakModal, setShowBreakModal] = useState(false);
  const [agentBreakHistory, setAgentBreakHistory] = useState({}); // Store break durations for admin view

  // Filter agents based on user role
  const displayAgents = userRoleIsAdmin 
    ? filteredAgents 
    : filteredAgents.filter(agent => agent.agent_number === user?.agent_number);

  // Get current time in HH:MM format
  const getCurrentTime = () => {
    const now = new Date();
    return now.toTimeString().slice(0, 5);
  };

  // Set current time as default from time when modal opens
  useEffect(() => {
    if (showBreakModal) {
      setBreakData(prev => ({
        ...prev,
        fromTime: getCurrentTime()
      }));
    }
  }, [showBreakModal]);

  const handleActiveStatusChange = (status) => {
    if (status === 'Break') {
      setShowBreakModal(true);
    } else if (status === 'Active' && isOnBreak) {
      // Calculate total break duration and push to backend
      const endTime = new Date();
      const totalDurationSeconds = Math.floor((endTime.getTime() - breakStartTime.getTime()) / 1000);
      const totalDurationMinutes = Math.round(totalDurationSeconds / 60);
      
      // Prepare break record for backend
      const breakRecord = {
        agent_number: user.agent_number,
        break_start: breakStartTime.toISOString(),
        break_end: endTime.toISOString(),
        duration_seconds: totalDurationSeconds,
        duration_minutes: totalDurationMinutes,
        remark: breakData.remark,
        from_time: breakData.fromTime,
        to_time: breakData.toTime
      };
      
      console.log('Break record to be saved:', breakRecord);
      // TODO: Replace with actual API call
      // await saveBreakRecord(breakRecord);
      
      // Store break duration for admin view
      setAgentBreakHistory(prev => ({
        ...prev,
        [user.agent_number]: {
          lastBreakDuration: totalDurationMinutes,
          lastBreakEnd: endTime,
          remark: breakData.remark
        }
      }));
      
      // Reset break state
      setIsOnBreak(false);
      setBreakStartTime(null);
      setBreakData({ fromTime: '', toTime: '', remark: '' });
      setActiveStatus('Active');
    } else {
      setActiveStatus(status);
    }
  };

  const startBreak = () => {
    if (breakData.fromTime && breakData.toTime && breakData.remark && isValidFromTime(breakData.fromTime)) {
      setIsOnBreak(true);
      setBreakStartTime(new Date());
      setActiveStatus('Break');
      setShowBreakModal(false);
    }
  };

  // Validate that from time is not in the past
  const isValidFromTime = (timeString) => {
    if (!timeString) return false;
    
    const now = new Date();
    const currentTime = now.getHours() * 60 + now.getMinutes();
    const [hours, minutes] = timeString.split(':').map(Number);
    const selectedTime = hours * 60 + minutes;
    
    return selectedTime >= currentTime;
  };

  // Format break duration for display
  const formatBreakDuration = (minutes) => {
    if (minutes < 60) {
      return `${minutes} min`;
    }
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours}h ${remainingMinutes}m`;
  };

  // Handle agent activation/deactivation
  const handleAgentStatusToggle = async (agentNumber, currentStatus) => {
    const newStatus = currentStatus === 'Active' ? 'Inactive' : 'Active';
    // TODO: Replace with actual API call
    console.log(`Toggling agent ${agentNumber} from ${currentStatus} to ${newStatus}`);
    // await updateAgentStatus(agentNumber, newStatus);
  };

  return (
    <div className="bg-white shadow rounded-lg">
      <div className="px-6 py-4 border-b border-gray-200">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
          <h2 className="text-lg font-medium text-gray-900 mb-4 sm:mb-0">
            {userRoleIsAdmin ? 'Agent Management Dashboard' : 'My Profile Dashboard'}
          </h2>
          
          <div className="flex flex-col sm:flex-row sm:items-center gap-3">
            {/* Admin Controls */}
            {userRoleIsAdmin && (
              <>
                {/* Search Bar - Admin Only */}
                <div className="relative">
                  <Search className="w-4 h-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                  <input
                    type="text"
                    placeholder="Search agents..."
                    value={agentSearch}
                    onChange={e => setAgentSearch(e.target.value)}
                    className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                
                {/* Add Agent Button */}
                <button
                  onClick={() => {
                    setShowAddModal(true);
                    setFormData({
                      agent_number: '',
                      name: '',
                      email: '',
                      password: '',
                      status: 'Active',
                      is_admin: false
                    });
                    setError('');
                  }}
                  className="flex items-center bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition-colors"
                >
                  <Plus className="w-4 h-4 mr-2" /> Add Agent
                </button>
              </>
            )}
            
            {/* Agent Status Controls - Agent Only */}
            {!userRoleIsAdmin && (
              <div className="flex items-center gap-3 bg-gray-50 px-4 py-2 rounded-lg">
                <label className="text-sm font-medium text-gray-700">Status:</label>
                <select
                  value={activeStatus}
                  onChange={(e) => handleActiveStatusChange(e.target.value)}
                  className="border-0 bg-transparent font-medium focus:outline-none focus:ring-2 focus:ring-blue-500 rounded px-2 py-1"
                >
                  <option value="Active">ðŸŸ¢ Active</option>
                  <option value="Break">ðŸŸ  Break</option>
                </select>
              </div>
            )}
            
            {/* Export Dropdown */}
            <div className="relative">
              <button
                onClick={() => setShowAgentExport(v => !v)}
                className="flex items-center bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
              >
                <Download className="w-4 h-4 mr-2" /> Export
              </button>
              {showAgentExport && (
                <div className="absolute right-0 mt-2 w-40 bg-white border border-gray-200 rounded-lg shadow-lg z-10">
                  <button className="block w-full text-left px-4 py-2 hover:bg-gray-100 rounded-t-lg" onClick={() => { exportToExcel(displayAgents, 'agents'); setShowAgentExport(false); }}>ðŸ“Š Excel</button>
                  <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToPDF(displayAgents, 'agents', ['agent_number','name','email','status','is_admin']); setShowAgentExport(false); }}>ðŸ“„ PDF</button>
                  <button className="block w-full text-left px-4 py-2 hover:bg-gray-100" onClick={() => { exportToCSV(displayAgents, 'agents'); setShowAgentExport(false); }}>ðŸ“‹ CSV</button>
                  <button className="block w-full text-left px-4 py-2 hover:bg-gray-100 rounded-b-lg" onClick={() => { exportToXML(displayAgents, 'agents'); setShowAgentExport(false); }}>ðŸ”– XML</button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Agent #</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
              {/* Admin-only columns */}
              {userRoleIsAdmin && (
                <>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Password</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Work Status</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Last Break</th>
                </>
              )}
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Account Status</th>
              {/* Agent-only columns */}
              {!userRoleIsAdmin && (
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Current Status</th>
              )}
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Role</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {displayAgents.map((agent) => (
              <tr key={agent.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                  {agent.agent_number}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {agent.name}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  {agent.email}
                </td>
                
                {/* Admin-only columns */}
                {userRoleIsAdmin && (
                  <>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      <code className="bg-gray-100 px-2 py-1 rounded text-xs">{agent.password}</code>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm">
                      {agent.agent_number === user?.agent_number && isOnBreak ? (
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800">
                          <Clock className="w-3 h-3 mr-1" />
                          On Break
                        </span>
                      ) : (
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                          âœ“ Working
                        </span>
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {agentBreakHistory[agent.agent_number] ? (
                        <div className="text-xs">
                          <div className="font-medium text-gray-900">
                            {formatBreakDuration(agentBreakHistory[agent.agent_number].lastBreakDuration)}
                          </div>
                          <div className="text-gray-500 truncate max-w-20" title={agentBreakHistory[agent.agent_number].remark}>
                            {agentBreakHistory[agent.agent_number].remark}
                          </div>
                        </div>
                      ) : (
                        <span className="text-gray-400">No breaks</span>
                      )}
                    </td>
                  </>
                )}
                
                <td className="px-6 py-4 whitespace-nowrap">
                  {userRoleIsAdmin ? (
                    <button
                      onClick={() => handleAgentStatusToggle(agent.agent_number, agent.status)}
                      className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium transition-colors ${
                        agent.status === 'Active' 
                          ? 'bg-green-100 text-green-800 hover:bg-green-200' 
                          : 'bg-red-100 text-red-800 hover:bg-red-200'
                      }`}
                    >
                      {agent.status === 'Active' ? 'âœ“ Active' : 'âœ— Inactive'}
                    </button>
                  ) : (
                    <span className={agentStatusClass(agent.status)}>{agent.status}</span>
                  )}
                </td>
                
                {/* Agent-only current status */}
                {!userRoleIsAdmin && agent.agent_number === user?.agent_number && (
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      activeStatus === 'Active' 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-orange-100 text-orange-800'
                    }`}>
                      {activeStatus === 'Active' ? 'ðŸŸ¢ Active' : 'ðŸŸ  Break'}
                    </span>
                  </td>
                )}
                
                <td className="px-6 py-4 whitespace-nowrap text-sm">
                  {agent.is_admin ? (
                    <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                      ðŸ‘‘ Admin
                    </span>
                  ) : (
                    <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
                      ðŸ‘¤ Agent
                    </span>
                  )}
                </td>
                
                <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                  <div className="flex items-center justify-end gap-2">
                    <button
                      onClick={() => openEditModal(agent)}
                      className="text-blue-600 hover:text-blue-900 p-1 rounded hover:bg-blue-50"
                      title="Edit Agent"
                    >
                      <Edit className="w-4 h-4" />
                    </button>
                    {/* Delete button - Admin only */}
                    {userRoleIsAdmin && (
                      <button
                        onClick={() => handleDeleteAgent(agent.agent_number)}
                        className="text-red-600 hover:text-red-900 p-1 rounded hover:bg-red-50"
                        disabled={deleteLoading}
                        title="Delete Agent"
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* No agents message */}
      {displayAgents.length === 0 && (
        <div className="text-center py-8 text-gray-500">
          <p>{userRoleIsAdmin ? 'No agents found.' : 'No profile data available.'}</p>
        </div>
      )}

      {/* Break Modal - Agent Only */}
      {showBreakModal && !userRoleIsAdmin && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-md relative">
            <button 
              className="absolute top-3 right-3 text-gray-400 hover:text-gray-600 text-xl" 
              onClick={() => {
                setShowBreakModal(false);
                setBreakData({ fromTime: '', toTime: '', remark: '' });
              }}
            >
              Ã—
            </button>
            <h3 className="text-xl font-bold mb-6 flex items-center gap-2 text-orange-600">
              <Pause className="w-6 h-6" />
              Schedule Break Time
            </h3>
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2 text-gray-700">From Time</label>
                  <input
                    type="time"
                    value={breakData.fromTime}
                    onChange={(e) => setBreakData({ ...breakData, fromTime: e.target.value })}
                    className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                    required
                  />
                  {breakData.fromTime && !isValidFromTime(breakData.fromTime) && (
                    <p className="text-red-500 text-xs mt-1">âš ï¸ Cannot select past time</p>
                  )}
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2 text-gray-700">To Time</label>
                  <input
                    type="time"
                    value={breakData.toTime}
                    onChange={(e) => setBreakData({ ...breakData, toTime: e.target.value })}
                    className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                    required
                  />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium mb-2 text-gray-700">Reason for Break</label>
                <input
                  type="text"
                  placeholder="e.g., Lunch Break, Team Meeting, Personal Break"
                  value={breakData.remark}
                  onChange={(e) => setBreakData({ ...breakData, remark: e.target.value })}
                  className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                  required
                />
              </div>
              <div className="flex gap-3 pt-4">
                <button
                  onClick={() => {
                    setShowBreakModal(false);
                    setBreakData({ fromTime: '', toTime: '', remark: '' });
                  }}
                  className="flex-1 bg-gray-500 text-white py-2 px-4 rounded-lg hover:bg-gray-600 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={startBreak}
                  className="flex-1 bg-orange-500 text-white py-2 px-4 rounded-lg hover:bg-orange-600 transition-colors flex items-center justify-center gap-2"
                  disabled={!breakData.fromTime || !breakData.toTime || !breakData.remark || !isValidFromTime(breakData.fromTime)}
                >
                  <Play className="w-4 h-4" />
                  Start Break
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Add Agent Modal - Admin Only */}
      {showAddModal && userRoleIsAdmin && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl p-8 w-full max-w-md relative">
            <button className="absolute top-3 right-3 text-gray-400 hover:text-gray-600 text-xl" onClick={() => setShowAddModal(false)}>Ã—</button>
            <h3 className="text-xl font-bold mb-6 text-green-600">Add New Agent</h3>
            <form className="space-y-4" onSubmit={handleAddAgent}>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-700">Agent Number</label>
                <input type="text" className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500" value={formData.agent_number} onChange={e => setFormData({ ...formData, agent_number: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-700">Full Name</label>
                <input type="text" className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500" value={formData.name} onChange={e => setFormData({ ...formData, name: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-700">Email Address</label>
                <input type="email" className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500" value={formData.email} onChange={e => setFormData({ ...formData, email: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-700">Password</label>
                <input type="text" className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500" value={formData.password} onChange={e => setFormData({ ...formData, password: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-700">Account Status</label>
                <select className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500" value={formData.status} onChange={e => setFormData({ ...formData, status: e.target.value })}>
                  <option value="Active">Active</option>
                  <option value="Inactive">Inactive</option>
                </select>
              </div>
              <div className="flex items-center">
                <input type="checkbox" id="is_admin_add" checked={formData.is_admin} onChange={e => setFormData({ ...formData, is_admin: e.target.checked })} className="mr-3" />
                <label htmlFor="is_admin_add" className="text-sm text-gray-700">Grant Admin Privileges</label>
              </div>
              {error && <div className="text-red-500 text-sm bg-red-50 p-2 rounded">{error}</div>}
              <button type="submit" className="w-full bg-green-600 text-white py-2 rounded-lg hover:bg-green-700 transition-colors" disabled={loading}>
                {loading ? 'Adding Agent...' : 'Add Agent'}
              </button>
            </form>
          </div>
        </div>
      )}
      
      {/* Edit Agent Modal */}
      {showEditModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl p-8 w-full max-w-md relative">
            <button className="absolute top-3 right-3 text-gray-400 hover:text-gray-600 text-xl" onClick={() => setShowEditModal(false)}>Ã—</button>
            <h3 className="text-xl font-bold mb-6 text-blue-600">Edit Agent Profile</h3>
            <form className="space-y-4" onSubmit={handleEditAgent}>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-700">Agent Number</label>
                <input type="text" className="w-full border border-gray-300 px-3 py-2 rounded-lg bg-gray-100" value={formData.agent_number} disabled />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-700">Full Name</label>
                <input type="text" className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" value={formData.name} onChange={e => setFormData({ ...formData, name: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-700">Email Address</label>
                <input type="email" className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" value={formData.email} onChange={e => setFormData({ ...formData, email: e.target.value })} required />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-700">Password (leave blank to keep unchanged)</label>
                <input type="text" className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" value={formData.password} onChange={e => setFormData({ ...formData, password: e.target.value })} />
              </div>
              {/* Admin-only fields in edit modal */}
              {userRoleIsAdmin && (
                <>
                  <div>
                    <label className="block text-sm font-medium mb-1 text-gray-700">Account Status</label>
                    <select className="w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" value={formData.status} onChange={e => setFormData({ ...formData, status: e.target.value })}>
                      <option value="Active">Active</option>
                      <option value="Inactive">Inactive</option>
                    </select>
                  </div>
                  <div className="flex items-center">
                    <input type="checkbox" id="is_admin_edit" checked={formData.is_admin} onChange={e => setFormData({ ...formData, is_admin: e.target.checked })} className="mr-3" />
                    <label htmlFor="is_admin_edit" className="text-sm text-gray-700">Grant Admin Privileges</label>
                  </div>
                </>
              )}
              {error && <div className="text-red-500 text-sm bg-red-50 p-2 rounded">{error}</div>}
              <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition-colors" disabled={loading}>
                {loading ? 'Saving Changes...' : 'Save Changes'}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AgentsTable;
