import React, { useState, useEffect } from 'react';
import { Edit, Trash2, Plus, Download, Clock, Play, Pause, Search } from 'lucide-react';
import { agentStatusClass } from '../utils/format'; // adjust path if needed

const AgentsTable = ({
  user,
  agentSearch,
  setAgentSearch,
  showAgentExport,
  setShowAgentExport,
  filteredAgents,
  exportToExcel,
  exportToPDF,
  exportToCSV,
  exportToXML,
  setShowAddModal,
  setFormData,
  setError,
  userRoleIsAdmin,
  openEditModal,
  handleDeleteAgent,
  deleteLoading,
  showAddModal,
  showEditModal,
  formData,
  setShowEditModal,
  handleAddAgent,
  handleEditAgent,
  loading,
  error
}) => {
  const [activeStatus, setActiveStatus] = useState('Active');
  const [breakData, setBreakData] = useState({ fromTime: '', toTime: '', remark: '' });
  const [isOnBreak, setIsOnBreak] = useState(false);
  const [breakStartTime, setBreakStartTime] = useState(null);
  const [showBreakModal, setShowBreakModal] = useState(false);
  const [agentBreakHistory, setAgentBreakHistory] = useState({});

  const displayAgents = user?.is_admin 
    ? filteredAgents 
    : filteredAgents.filter(agent => agent.agent_number === user?.agent_number);

  const getCurrentTime = () => {
    const now = new Date();
    return now.toTimeString().slice(0, 5);
  };

  useEffect(() => {
    if (showBreakModal) {
      setBreakData(prev => ({ ...prev, fromTime: getCurrentTime() }));
    }
  }, [showBreakModal]);

  const isValidFromTime = (timeString) => {
    if (!timeString) return false;
    const now = new Date();
    const currentTime = now.getHours() * 60 + now.getMinutes();
    const [hours, minutes] = timeString.split(':').map(Number);
    const selectedTime = hours * 60 + minutes;
    return selectedTime >= currentTime;
  };

  const handleActiveStatusChange = (status) => {
    if (status === 'Break') {
      setShowBreakModal(true);
    } else if (status === 'Active' && isOnBreak) {
      const endTime = new Date();
      const totalDurationSeconds = Math.floor((endTime.getTime() - breakStartTime.getTime()) / 1000);
      const totalDurationMinutes = Math.round(totalDurationSeconds / 60);

      const breakRecord = {
        agent_number: user.agent_number,
        break_start: breakStartTime.toISOString(),
        break_end: endTime.toISOString(),
        duration_seconds: totalDurationSeconds,
        duration_minutes: totalDurationMinutes,
        remark: breakData.remark,
        from_time: breakData.fromTime,
        to_time: breakData.toTime
      };

      console.log('Break record to be saved:', breakRecord);

      setAgentBreakHistory(prev => ({
        ...prev,
        [user.agent_number]: {
          lastBreakDuration: totalDurationMinutes,
          lastBreakEnd: endTime,
          remark: breakData.remark
        }
      }));

      setIsOnBreak(false);
      setBreakStartTime(null);
      setBreakData({ fromTime: '', toTime: '', remark: '' });
      setActiveStatus('Active');
    } else {
      setActiveStatus(status);
    }
  };

  const startBreak = () => {
    if (breakData.fromTime && breakData.toTime && breakData.remark && isValidFromTime(breakData.fromTime)) {
      setIsOnBreak(true);
      setBreakStartTime(new Date());
      setActiveStatus('Break');
      setShowBreakModal(false);
    }
  };

  const formatBreakDuration = (minutes) => {
    if (minutes < 60) return `${minutes} min`;
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours}h ${remainingMinutes}m`;
  };

  const handleAgentStatusToggle = async (agentNumber, currentStatus) => {
    const newStatus = currentStatus === 'Active' ? 'Inactive' : 'Active';
    console.log(`Toggling agent ${agentNumber} from ${currentStatus} to ${newStatus}`);
    // await updateAgentStatus(agentNumber, newStatus);
  };

  return (
    <>
      {/* AGENT STATUS SWITCH */}
      {!userRoleIsAdmin && (
        <div className="flex justify-end items-center gap-3 mb-3">
          <label className="text-sm text-gray-700">Status:</label>
          <select
            value={activeStatus}
            onChange={(e) => handleActiveStatusChange(e.target.value)}
            className="border px-3 py-1 rounded"
          >
            <option value="Active">🟢 Active</option>
            <option value="Break">🟠 Break</option>
          </select>
        </div>
      )}

      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200 border rounded">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-2 text-left text-sm font-semibold">Agent #</th>
              <th className="px-4 py-2 text-left text-sm font-semibold">Name</th>
              <th className="px-4 py-2 text-left text-sm font-semibold">Email</th>
              <th className="px-4 py-2 text-left text-sm font-semibold">Status</th>
              <th className="px-4 py-2 text-left text-sm font-semibold">Role</th>
              {userRoleIsAdmin && (
                <>
                  <th className="px-4 py-2 text-left text-sm font-semibold">Last Break</th>
                  <th className="px-4 py-2 text-left text-sm font-semibold">Actions</th>
                </>
              )}
            </tr>
          </thead>
          <tbody>
            {displayAgents.map(agent => (
              <tr key={agent.agent_number} className="hover:bg-gray-50">
                <td className="px-4 py-2">{agent.agent_number}</td>
                <td className="px-4 py-2">{agent.name}</td>
                <td className="px-4 py-2">{agent.email}</td>
                <td className="px-4 py-2">
                  {userRoleIsAdmin ? (
                    <button
                      onClick={() => handleAgentStatusToggle(agent.agent_number, agent.status)}
                      className={`px-2 py-1 rounded text-xs ${
                        agent.status === 'Active' ? 'bg-green-200' : 'bg-red-200'
                      }`}
                    >
                      {agent.status}
                    </button>
                  ) : (
                    <span className={agentStatusClass(agent.status)}>{agent.status}</span>
                  )}
                </td>
                <td className="px-4 py-2">{agent.is_admin ? 'Admin' : 'Agent'}</td>
                {userRoleIsAdmin && (
                  <>
                    <td className="px-4 py-2 text-xs text-gray-500">
                      {agentBreakHistory[agent.agent_number]
                        ? `${formatBreakDuration(agentBreakHistory[agent.agent_number].lastBreakDuration)} (${agentBreakHistory[agent.agent_number].remark})`
                        : '—'}
                    </td>
                    <td className="px-4 py-2 flex gap-2">
                      <button onClick={() => openEditModal(agent)} className="text-blue-500"><Edit size={16} /></button>
                      <button onClick={() => handleDeleteAgent(agent.agent_number)} className="text-red-500"><Trash2 size={16} /></button>
                    </td>
                  </>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Break Modal */}
      {showBreakModal && !userRoleIsAdmin && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
            <h2 className="text-lg font-semibold mb-4 flex items-center text-orange-500">
              <Pause className="mr-2" /> Schedule Break
            </h2>
            <label className="block text-sm font-medium">From Time</label>
            <input
              type="time"
              value={breakData.fromTime}
              onChange={(e) => setBreakData({ ...breakData, fromTime: e.target.value })}
              className="w-full mb-2 border px-3 py-2 rounded"
            />
            {!isValidFromTime(breakData.fromTime) && (
              <p className="text-xs text-red-500 mb-2">⚠ Cannot select past time</p>
            )}
            <label className="block text-sm font-medium">To Time</label>
            <input
              type="time"
              value={breakData.toTime}
              onChange={(e) => setBreakData({ ...breakData, toTime: e.target.value })}
              className="w-full mb-2 border px-3 py-2 rounded"
            />
            <label className="block text-sm font-medium">Reason</label>
            <input
              type="text"
              value={breakData.remark}
              onChange={(e) => setBreakData({ ...breakData, remark: e.target.value })}
              className="w-full mb-4 border px-3 py-2 rounded"
              placeholder="e.g., Lunch, Meeting"
            />
            <div className="flex justify-between">
              <button onClick={() => setShowBreakModal(false)} className="bg-gray-400 text-white px-4 py-2 rounded">Cancel</button>
              <button
                onClick={startBreak}
                className="bg-orange-500 text-white px-4 py-2 rounded"
                disabled={!breakData.fromTime || !breakData.toTime || !breakData.remark || !isValidFromTime(breakData.fromTime)}
              >
                <Play className="inline-block mr-1" size={16} /> Start
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default AgentsTable;
