import React, { useEffect, useState } from 'react';
import { fetchCompanyInfo } from '../utils/api';

export default function CompanyExportGuard({ children }) {
  const [allowed, setAllowed] = useState(true);
  const [checked, setChecked] = useState(false);

  useEffect(() => {
    const run = async () => {
      const token = localStorage.getItem('token');
      const company = await fetchCompanyInfo(token);
      if (!company) {
        setAllowed(true);
      } else {
        const isPartiallyClosed = company.status === 'Partially Close';
        setAllowed(true);
      }
      setChecked(true);
    };
    run();
  }, []);

  if (!checked) return null;
  if (!allowed) {
    return (
      <button className="ml-2 bg-gray-300 text-gray-600 px-3 py-1 rounded cursor-not-allowed" title="Export disabled (Partially Close)">
        Export
      </button>
    );
  }
  return children;
}
